
DROP TABLE IF EXISTS USER;
CREATE TABLE USER (ID VARCHAR PRIMARY KEY, SEX VARCHAR, NICKNAME VARCHAR, PROFILE_PHOTO VARCHAR, BIRTHDAY VARCHAR);



DROP TABLE IF EXISTS COMMUNITY_BASE;
CREATE TABLE COMMUNITY_BASE(ID INT PRIMARY KEY AUTO_INCREMENT, UPR_ID INT, DEPTH INT, TITLE VARCHAR, CONTENTS VARCHAR, HIT INT, LIKED INT, DISLIKED INT, REG_ID VARCHAR);



